// scripts/scalar-utils.js

window.scalarUtils = {
    debug: !![/^127\.0\.0\.1$/, /^localhost$/, /^staging-.*$/, /^alpha-.*$/].find(r => r.test(window.location.hostname)),
    debugLog: function (...args) {
        if (!scalarUtils.debug) return;
        const err = new Error();
        const stack = err.stack?.split('\n');
        const callerLine = stack?.[2]?.trim();
        console.log('[debug]', ...args);
        if (callerLine) {
            console.log('\tâ†³ called from', callerLine);
        }
    },
    appendScript: function (src) {
        const script = document.createElement('script');
        script.src = src;
        script.async = true;
        if (document.head) {
            document.head.appendChild(script);
        } else {
            document.appendChild(script);
        }
        return new Promise(resolve => {
            script.onload = () => resolve(script);
        })
    }, appendModule: function (moduleName) {
        const src = `https://unpkg.com/${moduleName}`
        return scalarUtils.appendScript(src).then(() => window[moduleName]);
    }, pathMatch: function (pathFilter) {
        const endSlash = pathFilter.endsWith('/')
        if (endSlash) {
            pathFilter = pathFilter.substring(0, pathFilter.length - 1)
        }
        const path = window.location.pathname;
        if (pathFilter === path) {
            return true
        } else {
            return endSlash && path.startsWith(pathFilter + '/');
        }
    },
    dynamicDownload: (href, filename) => {
        let hashFn = (str) => {
            let hash = 0, i = 0, len = str.length;
            while (i < len)  hash = ((hash << 5) - hash + str.charCodeAt(i++)) << 0;
            return hash
        }
        let id = `x${hashFn(href)}${hashFn(filename)}`
        let element = document.querySelector(`#${id}`);
        if (element == null) {
            element = document.createElement('a');
            element.setAttribute('id', id);
            element.setAttribute('href', href);
            element.setAttribute('download', filename);
            element.setAttribute('style', 'display:none');
            if (!href.startsWith('data:')) {
                element.setAttribute('target', '_blank');
            }
            document.head.appendChild(element);
        }
        element.click();
    }
}
const waitForTheElementModule = scalarUtils.appendModule('wait-for-the-element')
scalarUtils.waitFor = function (selector, options) {
    const node = document.querySelector(selector);
    if (node) return Promise.resolve(node)
    return waitForTheElementModule.then(module => {
        // noinspection JSUnresolvedReference
        return module.waitForTheElement(selector, options || {});
    })
}

function onUrlChange(callback) {
    const originalPushState = history.pushState;
    const originalReplaceState = history.replaceState;
    function wrap(fn) {
        return function (...args) {
            const result = fn.apply(this, args);
            callback();
            return result;
        };
    }
    history.pushState = wrap(originalPushState);
    history.replaceState = wrap(originalReplaceState);
    window.addEventListener('popstate', callback);
}

scalarUtils.onReady = function (listener, pathFilter, disablePopstate) {
    if (typeof listener !== 'function') return;
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', () => scalarUtils.onReady(listener));
    } else {
        let callback;
        if (pathFilter) {
            callback = () => {
                if (scalarUtils.pathMatch(pathFilter)) listener()
            }
        } else {
            callback = listener
        }
        if (!disablePopstate) {
            onUrlChange(callback);
        }
        return callback()
    }
}

})();

