// scripts/hash-navigation.js

function apiHashchange(event) {
    const hash = window.location.hash;
    if ((hash?.length || 0) < 2) return;
    const node = document.querySelector(`#${CSS.escape(hash.substring(1))}`);
    if (node) {
        scalarUtils.debugLog('hash node found', hash, node);
    } else {
        const urlBase = window.location.origin + window.location.pathname;
        const href = urlBase + hash;
        scalarUtils.debugLog('hash node not found', hash, href);
        window.location.href = href
        window.location.reload()
    }
}

scalarUtils.onReady(() => {
    addEventListener("hashchange", apiHashchange);
}, '/api/', true)

})();

