(async()=>{

// scripts/api-sidebar-link.js

const apiPath = '/api'

function selectApiHeaderLink() {
    return scalarUtils.waitFor(`a.header-item-link[href="${apiPath}"]`)
}

function selectApiSidebarLink() {
    return scalarUtils.waitFor(`.sidebar-group-item a[href="${apiPath}"]`)
}

const apiSidebarLinkConfig = async () => {
    const apiSidebarLink = await selectApiSidebarLink()
    if (!apiSidebarLink) {
        return 'api_sidebar_link_not_found';
    } else {
        const apiHeaderLink = await selectApiHeaderLink()
        if (!apiHeaderLink) {
            return 'api_header_link_not_found';
        } else {
            apiSidebarLink.setAttribute('target', '_self');
            apiSidebarLink.onclick = event => {
                scalarUtils.debugLog('api sidebar link clicked', event)
                event.preventDefault();
                apiHeaderLink.click()
            }
            return 'api_sidebar_link_updated';
        }
    }
}
scalarUtils.onReady(async () => {
    scalarUtils.debugLog(`api sidebar link config:${await apiSidebarLinkConfig()}`);
}, '/guides/')

})();

