// scripts/api-reference-redirect.js

function redirectIfApiReferenceMatch(path) {
    path = path || window.location.pathname;
    if (!path.startsWith("/api-reference/")) return;
    const parts = path.replace("/api-reference/", "").split("/");
    if (parts.length < 2) return;
    const tagSlug = parts[0];
    const operationIdSlug = parts.slice(1).join("/");
    const operationContext = operationContexts.find(ctx =>
        ctx.tagSlugs.includes(tagSlug) && ctx.operationIdSlug === operationIdSlug
    );
    if (!operationContext) return;
    const redirectUrl = `/api#tag/${operationContext.tagSlugs[0]}/${operationContext.method}${operationContext.path}`;
    scalarUtils.debugLog(`redirecting to ${redirectUrl}`, operationContext);
    window.location.href = redirectUrl;
}


scalarUtils.onReady(() => {
}, null, true)

})();

(async()=>{

// scripts/spec-download.js

scalarUtils.onReady(()=>{
    window.openapiSpecUrl = (type) => {
        return window[`openapi_spec_url_${type}`]
    }
    window.openapiSpecDownload = (type, filename = null) => {
        scalarUtils.dynamicDownload(openapiSpecUrl(type), filename ? filename : `openapi.${type}`)
    }
    window.openapiPdfSpecGenerate = () => {
        let rapiPdfId = 'openapi-rapi-pdf'
        let generateTask = () => {
            let url = openapiSpecUrl('json');
            let spec;
            if (url.startsWith('data:')) {
                spec = JSON.parse(decodeURIComponent(url.split(',')[1]));
            } else {
                spec = url
            }
            document.querySelector(`#${rapiPdfId}`).generatePdf(spec);
        };
        if (document.querySelector(`#${rapiPdfId}`) == null) {
            let rapiPdf = document.createElement('rapi-pdf');
            rapiPdf.setAttribute('id', rapiPdfId);
            rapiPdf.setAttribute('style', 'display:none');
            rapiPdf.setAttribute('pdf-primary-color', '#161658');
            rapiPdf.setAttribute('pdf-alternate-color', '#6563da');
            document.body.appendChild(rapiPdf);
            let script = document.createElement('script');
            script.src = 'https://unpkg.com/rapipdf/dist/rapipdf-min.js';
            script.onload = generateTask;
            document.body.appendChild(script);
        } else {
            generateTask();
        }
    }
},null, true)

})();
